CREATE   FUNCTION [dbo].[fnTableExists](@qrn VARCHAR(60))
RETURNS BIT
AS
BEGIN
   DECLARE
       @schema    VARCHAR(10)
      ,@table_nm  VARCHAR(60)
   ;

   SELECT
       @schema    = schema_nm
      ,@table_nm  = rtn_nm
   FROM fnSplitQualifiedName(@qrn);

   RETURN iif(EXISTS (SELECT 1 FROM INFORMATION_SCHEMA.TABLES WHERE TABLE_NAME = @table_nm AND TABLE_SCHEMA = @schema), 1, 0);
END


