      ALTER TABLE [dbo].[staging2] CHECK CONSTRAINT [FK_staging2_staging1]

      ------------------------------------------------------------------------------
      -- 3. R02: Incrementally import the File to S1
      ------------------------------------------------------------------------------
      if @is_xl = 1
      BEGIN
