CREATE PROCEDURE [dbo].[sp_import_PestHandlers]
    @file            VARCHAR(500)
   ,@folder          VARCHAR(600) = NULL
   ,@display_tables  BIT          = 0
AS
BEGIN
   DECLARE
    @fn                 VARCHAR(35)    = N'import_Pest_Handlers'
   ,@sql                VARCHAR(MAX)
   ,@bkslsh             CHAR(1)       = CHAR(92)
   ,@cmd                VARCHAR(MAX)
   ,@error_file         VARCHAR(400)   = NULL
   ,@error_msg          VARCHAR(MAX)   = NULL
