   ,@null_type_row_cnt  INT            = -1
   ;

   SET NOCOUNT OFF
   BEGIN TRY
      EXEC sp_log 1, @fn, '000: starting:
file          :[', @file  ,']
folder        :[', @folder,']
display_tables:[', @display_tables,']
';

      ---------------------------------------
      -- Validate inputs
      ---------------------------------------
      IF @folder IS NOT NULL
         SET @file = CONCAT(@folder, @bkslsh, @file);

      ----------------------------------------------------------------------------------
      -- Process
