CREATE FUNCTION [dbo].[fnCamelCase]( @s VARCHAR(MAX))
  RETURNS VARCHAR(MAX)
AS
BEGIN 
   DECLARE @Position INT;

   IF (@s IS NULL) OR (@s='')
      RETURN @s;

   SET @s = CONCAT(Upper(Left(@s, 1)), LOWER(SUBSTRING(@s, 2, dbo.fnLen(@s)-1)));
   RETURN @s;
END;
